  //       },
  //     });
  //     console.log('User profile:', response.data);
  //     setUserProfile(response.data);
  //   } catch (error) {
  //     console.error('Error fetching user profile:', error);
  //   }
  // };

  // const handleFetchUserQuestions = async (userId) => {
  //   try {
  //     const accessToken = localStorage.getItem('accessToken');
  //     const response = await axios.get(`${API_URL}/users/${userId}/questions`, {
  //       headers: {
  //         Authorization: `Bearer ${accessToken}`,
  //       },
  //     });
  //     console.log('User questions:', response.data);
  //     setUserQuestions(response.data);
  //   } catch (error) {
  //     console.error('Error fetching user questions:', error);
  //   }
  // };

  // Determine which component to show based on the appState
  return (
    <div className="App">
      {appState === 'registration' && (
        <UserRegistration API_URL={API_URL} onRegister={handleRegister} />
      )}

      {appState === 'login' && (
        <UserLogin API_URL={API_URL} onLogin={handleLogin} />
      )}

      {appState === 'loggedIn' && (
        <>
          <NavBar userId={userId} onLogout={handleLogout} />
          <AskQuestion API_URL={API_URL}  />
          <UserQuestions API_URL={API_URL} userId={userId} />
        </>
      )}
    </div>
  );
}

export default App;
