import React, { useState } from 'react';
import axios from 'axios';
import UserRegistration from './UserRegistration';
import UserLogin from './UserLogin';
import NavBar from './NavBar';
import AskQuestion from './AskQuestion';
import UserQuestions from './UserQuestions';

const API_URL = process.env.REACT_APP_API_URL;

function App() {
  const [userId, setUserId] = useState('');
  const [userProfile, setUserProfile] = useState({});
  const [userQuestions, setUserQuestions] = useState([]);
  const [askedQuestion, setAskedQuestion] = useState('');

  const handleFetchUserProfile = async (userId) => {
    try {
      const accessToken = localStorage.getItem('accessToken');
      const response = await axios.get(`${API_URL}/users/${userId}`, {
        headers: {
          Authorization: `Bearer ${accessToken}`,
        },
      });
      console.log('User profile:', response.data);
      setUserProfile(response.data);
    } catch (error) {
      console.error('Error fetching user profile:', error);
    }
  };

  const handleFetchUserQuestions = async (userId) => {
    try {
      const accessToken = localStorage.getItem('accessToken');
      const response = await axios.get(`${API_URL}/users/${userId}/questions`, {
        headers: {
          Authorization: `Bearer ${accessToken}`,
        },
      });
      console.log('User questions:', response.data);
      setUserQuestions(response.data);
    } catch (error) {
      console.error('Error fetching user questions:', error);
    }
  };

  return (
    <div className="App">
      <NavBar userId={userId} />

      <UserRegistration
        API_URL={API_URL}

      />

      <UserLogin
        API_URL={API_URL}

      />

      <AskQuestion
         API_URL={API_URL}
      />

      <UserQuestions
         API_URL={API_URL}
        userId={userId}

      />

      <div>
        {Object.keys(userProfile).length > 0 ? (
          <div>
            {Object.entries(userProfile)
              .filter(([key]) => key === 'email')
              .map(([key, value]) => (
                <p key={key}>
                  <strong>Email:</strong> {value}
                </p>
              ))}
          </div>
        ) : (
          <p>No user profile available</p>
        )}
      </div>
    </div>
  );
}

export default App;
