import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UserQuestions = ({ API_URL, userId }) => {
  const [userQuestions, setUserQuestions] = useState([]);

  useEffect(() => {
    const fetchUserQuestions = async () => {
      try {
        const accessToken = localStorage.getItem('accessToken');
        const response = await axios.get(`${API_URL}/users/${userId}/questions`, {
          headers: {
            Authorization: `Bearer ${accessToken}`,
          },
        });
        console.log('User questions:', response.data);
        setUserQuestions(response.data);
      } catch (error) {
        console.error('Error fetching user questions:', error);
      }
    };

    fetchUserQuestions();
  }, [API_URL, userId]);

  return (
    <div className=" mt-4">
      <h1 className="mb-4">User Questions</h1>
      <div className="list-group">
        {userQuestions.map((userQuestion, index) => (
          <div key={index} className="list-group-item">
            <h5 className="mb-3">
              <strong>Question {index + 1}:</strong> {userQuestion.question}
            </h5>
          
          </div>
        ))}
      </div>
    </div>
  );
};

export default UserQuestions;
