import React, { useState } from 'react';
import axios from 'axios';

const UserQuestions = ({ API_URL, userId }) => {
  const [userQuestions, setUserQuestions] = useState([]);
  const [error, setError] = useState('');

  const handleFetchUserQuestions = async (userId) => {
    try {
      const accessToken = localStorage.getItem('accessToken');
      const response = await axios.get(`${API_URL}/users/${userId}/questions`, {
        headers: {
          Authorization: `Bearer ${accessToken}`,
        },
      });
      console.log('User questions:', response.data);
      setUserQuestions(response.data);
      setError(''); // Clear any previous errors
    } catch (error) {
      console.error('Error fetching user questions:', error);
      setError('Failed to fetch user questions. Please try again.'); // Set error message
    }
  };

  return (
    <div>
      <h1>User Questions</h1>
      <button onClick={() => handleFetchUserQuestions(userId)}>Fetch User Questions</button>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {userQuestions.map((userQuestion, index) => (
        <div key={index}>
          <p><strong>Question {index + 1}:</strong> {userQuestion.question}</p>
        </div>
      ))}
    </div>
  );
};

export default UserQuestions;
