import React, { useState } from 'react';
import axios from 'axios';
import UserRegistration from './components/UserRegistration';
import UserLogin from './components/UserLogin';
import NavBar from './components/NavBar';
import AskQuestion from './components/AskQuestion';
import UserQuestions from './components/UserQuestions';

const API_URL = process.env.REACT_APP_API_URL;

function App() {
  const [userId, setUserId] = useState('');
  const [appState, setAppState] = useState('registration'); // new state to manage the app flow
  const [userProfile, setUserProfile] = useState({});
  const [userQuestions, setUserQuestions] = useState([]);
  const [askedQuestion, setAskedQuestion] = useState('');

  const handleRegister = async (email, password) => {
    try {
      const response = await axios.post(`${API_URL}/users`, {
        email,
        password,
      });
      console.log('User registered:', response.data);

      setUserId(response.data.id);
      setAppState('login'); // Move to login state
    } catch (error) {
      console.error('Error registering user:', error);
    }
  };

  const handleLogin = async (email, password) => {
    try {
      const response = await axios.post(`${API_URL}/auth/login`, {
        email,
        password,
      });
      localStorage.setItem('accessToken', response.data.accessToken);
      // Assuming the response contains userId
      setUserId(response.data.id);
      console.log('User logged in:', response.data);
      setAppState('loggedIn'); // Move to loggedIn state
    } catch (error) {
      console.error('Error logging in:', error);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('accessToken');
    setUserId('');
    setAppState('login'); // Move back to login state after logout
    console.log('User logged out');
  };

  const handleFetchUserProfile = async (userId) => {
    try {
      const accessToken = localStorage.getItem('accessToken');
      const response = await axios.get(`${API_URL}/users/${userId}`, {
        headers: {
          Authorization: `Bearer ${accessToken}`,
        },
      });
      console.log('User profile:', response.data);
      setUserProfile(response.data);
    } catch (error) {
      console.error('Error fetching user profile:', error);
    }
  };

  const handleFetchUserQuestions = async (userId) => {
    try {
      const accessToken = localStorage.getItem('accessToken');
      const response = await axios.get(`${API_URL}/users/${userId}/questions`, {
        headers: {
          Authorization: `Bearer ${accessToken}`,
        },
      });
      console.log('User questions:', response.data);
      setUserQuestions(response.data);
    } catch (error) {
      console.error('Error fetching user questions:', error);
    }
  };

  // Determine which component to show based on the appState
  return (
    <div className="App">
      {appState === 'registration' && (
        <UserRegistration API_URL={API_URL} onRegister={handleRegister} />
      )}

      {appState === 'login' && (
        <UserLogin API_URL={API_URL} onLogin={handleLogin} />
      )}

      {appState === 'loggedIn' && (
        <>
          <NavBar userId={userId} onLogout={handleLogout} />
          <AskQuestion API_URL={API_URL} setAskedQuestion={setAskedQuestion} />
          <UserQuestions API_URL={API_URL} userId={userId} />
        </>
      )}
    </div>
  );
}

export default App;
