import React, { useState } from 'react';
import axios from 'axios';

const AskQuestion = ({ API_URL }) => {
  const [question, setQuestion] = useState('');
  const [askedQuestion, setAskedQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  
  const handleAskQuestion = async () => {
    try {
      const accessToken = localStorage.getItem('accessToken');
      console.log("access token:", accessToken);
      const response = await axios.post(
        `${API_URL}/questions`,
        { question },
        {
          headers: {
            Authorization: `Bearer ${accessToken}`,
          },
        }
      );
      console.log('Question asked:', response.data);
      setAskedQuestion(response.data.question);
      setAnswer(response.data.answer);
    } catch (error) {
      console.error('Error asking question:', error);
    }
  };

  const renderAnswer = (text) => {
    const lines = text.split('\n');
    let currentList = [];
    let paragraphs = [];
  
    lines.forEach((line, index) => {
      const cleanedLine = line.replace(/\*/g, ''); // Remove all '*' characters
  
      if (line.trim().startsWith('* ')) { // Check if the original line starts with '* '
        currentList.push(<li key={index}>{cleanedLine.trim()}</li>);
      } else {
        if (currentList.length > 0) {
          paragraphs.push(<ul key={`list-${index}`}>{currentList}</ul>);
          currentList = [];
        }
        if (cleanedLine.trim() !== '') {
          const parts = cleanedLine.split(/(\*\*.*?\*\*)/g).map((part, partIndex) => {
            if (part.startsWith('**') && part.endsWith('**')) {
              return <strong key={partIndex}>{part.slice(2, -2)}</strong>;
            }
            return part;
          });
          paragraphs.push(<p key={`paragraph-${index}`}>{parts}</p>);
        }
      }
    });
  
    if (currentList.length > 0) {
      paragraphs.push(<ul key={`list-end`}>{currentList}</ul>);
    }
  
    return paragraphs;
  };
  
  
  

  return (
    <div>
      <h1>Ask a Question</h1>
      <input
        type="text"
        placeholder="Question"
        value={question}
        onChange={(e) => setQuestion(e.target.value)}
      />
      <button onClick={handleAskQuestion}>Ask Question</button>
      {askedQuestion && (
        <>
          <p>User asked: {askedQuestion}</p>
          <div>
            <h2>Answer:</h2>
            {renderAnswer(answer)}
          </div>
        </>
      )}
    </div>
  );
};

export default AskQuestion;
